import React, {useState, Fragment} from "react";
import './App.css';


function App() {
    const options = [
        {
            name: "Mails",
            blocks: [
                {
                    name: "FAQ"
                }, {
                    name: "Antwortblöcke"
                }, {
                    name: "Newsletter"
                }
            ]
        }, {
            name: "Positionen",
            blocks: [
                {
                    name: "Kasse",
                    blocks: [
                        {
                            name: "Check-In"
                        }, {
                            name: "Check-Out"
                        }
                    ]
                }, {
                    name: "Fenster",
                    blocks: [
                        {
                            name: "Check-In Pre"
                        }, {
                            name: "Check-In Runner-Zettel"
                        }, {
                            name: "Routen drucken"
                        }, {
                            name: "Packen"
                        }
                    ]
                }, {
                    name: "Runner",
                    blocks: [
                        {
                            name: "Vorbereitung"
                        }, {
                            name: "Routen"
                        }
                    ]
                }
            ]
        }, {
            name: "Schichtplan",
            blocks: [
                {
                    name: "Schichten ändern",
                }, {
                    name: "Schichten anlegen",
                }, {
                    name: "Schichtplan"
                }
            ]
        }, {
            name: "Ordner"
        }
    ]

    const setOptionsPosition = (block, c) => {
        block.forEach((block, i) => {
            Object.assign(block, {position: c+i.toString()});
            block.blocks && setOptionsPosition(block.blocks, `${c+i.toString()} ` )
        })
    }
    setOptionsPosition(options, "")

    const [renderBlock, statusRenderBlock] = useState([]);
    const [positionNav, statusPositionNav] = useState("0");

    const getNavPosition = (block, i) => {
        statusRenderBlock(block[i])
        //statusPositionNav(block[i].blocks ? block[i].blocks[0].position : block[0].position )
        statusPositionNav(block[i].position)
        console.log(block[i].position)
    }

    const iterateBlocks = (blocks) => (
        <ul>
            {
                Object.keys(blocks).map((i) => (
                    <Fragment key={i}>
                        { blocks[i].id && (
                            <li className="block">
                                <button onClick={() => statusRenderBlock(blocks[i])}>{blocks[i].name}</button>
                                <button onClick={() => getNavPosition(blocks, i)}>Get IDs</button>
                                { blocks[i].blocks && iterateBlocks(blocks[i].blocks)}
                            </li>
                        )}
                    </Fragment>
                ))
            }
        </ul>
    )

    const renderSidebar = (blocks) => (
        <ul>
            {Object.keys(blocks).map((i) => blocks[i].name && (
                <li key={i} className="block">
                    <button onClick={() => getNavPosition(blocks, i) }>{blocks[i].name}</button>
                    { blocks[i].blocks && positionNav.startsWith(blocks[i].position) && renderSidebar(blocks[i].blocks)}
                </li>
            ))}
        </ul>
    )

    return (
        <div id="SCC">
            <div id="Sidebar">
                {
                    renderSidebar(options)
                }
            </div>
            <div id="Content">
                {renderBlock && (
                    <div>
                        {renderBlock.name}
                        {renderBlock.blocks && renderSidebar(renderBlock.blocks)}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
