import React, {useState, Fragment} from "react";
import './App.css';


function App() {
    const options = [
        {
            name: "Mails",
            blocks: [
                {
                    name: "FAQ"
                }, {
                    name: "Antwortblöcke"
                }, {
                    name: "Newsletter"
                }
            ]
        }, {
            name: "Positionen",
            blocks: [
                {
                    name: "Kasse",
                    blocks: [
                        {
                            name: "Check-In"
                        }, {
                            name: "Check-Out"
                        }
                    ]
                }, {
                    name: "Fenster",
                    blocks: [
                        {
                            name: "Check-In Pre"
                        }, {
                            name: "Check-In Runner-Zettel"
                        }, {
                            name: "Routen drucken"
                        }, {
                            name: "Packen"
                        }
                    ]
                }, {
                    name: "Runner",
                    blocks: [
                        {
                            name: "Vorbereitung"
                        }, {
                            name: "Routen"
                        }
                    ]
                }
            ]
        }, {
            name: "Schichtplan",
            blocks: [
                {
                    name: "Schichten ändern",
                }, {
                    name: "Schichten anlegen",
                }, {
                    name: "Schichtplan"
                }
            ]
        }, {
            name: "Ordner"
        }
    ]
    const [renderBlock, statusRenderBlock] = useState(options[0]);
    const [hideNav, statusHideNav] = useState(false);
    const [sidebarWidth, statusSidebarWidth] = useState(0);

    // Modify options
    const setOptionsPosition = (blocks, c) => {
        blocks.forEach((block, i) => {
            Object.assign(block, {position: c+i.toString()});
            block.blocks && setOptionsPosition(block.blocks, `${c+i.toString()} ` )
        })
    }
    setOptionsPosition(options, "")

    // Get block.position parent blocks
    const parentBlocks = () => {
        let blocks = []
        let block = options
        renderBlock.position.split(" ").forEach((i, c) => {
            block = c === 0 ? options[i] : block.blocks[i]
            blocks = [...blocks, block]
        })
        return blocks
    }

    const renderNavButton = (block, k) => (
        <button key={k || 0}
                onClick={() => {
                    statusRenderBlock(block);
                    statusHideNav(false);
                    statusSidebarWidth(document.getElementById("Sidebar"))
                } }
                className={`sidebarButton ${renderBlock.position.startsWith(block.position) && "active"}`}>{block.name}</button>
    )

    const renderSidebar = (blocks) => (
        <ul>
            {Object.keys(blocks).map((i) => blocks[i].name && (
                <li key={i} className="navBlock">
                    {renderNavButton(blocks[i])}
                    { blocks[i].blocks && renderBlock.position.startsWith(blocks[i].position) && renderSidebar(blocks[i].blocks)}
                </li>
            ))}
        </ul>
    )

    return (
        <div id="SCC" style={{width: sidebarWidth}}>
            <div id="Sidebar" className={`${hideNav && "hidden"}`}>
                <button className={`hideSideBar ${ hideNav && "active"}`} onClick={() => statusHideNav(!hideNav)}>X</button>
                {
                    renderSidebar(options)
                }
            </div>
            <div id="TopBar">
                {parentBlocks().map((block, k) => renderNavButton(block, k) )}
            </div>
            <div id="Content">
                {renderBlock && (
                    <div>
                        {renderBlock.name}
                        {renderBlock.blocks && renderSidebar(renderBlock.blocks)}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
