import React, {useState} from "react";
import './App.css';

function App() {
    const options = [
        {
            id: 1,
            name: "Mails",
            blocks: [
                {
                    id: 1,
                    name: "FAQ"
                }, {
                    id: 2,
                    name: "Antwortblöcke"
                }, {
                    id: 3,
                    name: "Newsletter"
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 2,
            name: "Positionen",
            blocks: [
                {
                    id: 1,
                    name: "Kasse",
                    blocks: [
                        {
                            id: 1,
                            name: "Check-In"
                        }, {
                            id: 2,
                            name: "Check-Out"
                        }
                    ],
                    order: [1, 2]
                }, {
                    id: 2,
                    name: "Fenster",
                    blocks: [
                        {
                            id: 1,
                            name: "Check-In Pre"
                        }, {
                            id: 2,
                            name: "Check-In Runner-Zettel"
                        }, {
                            id: 3,
                            name: "Routen drucken"
                        }, {
                            id: 4,
                            name: "Packen"
                        }
                    ],
                    order: [1, 2, 3, 4]
                }, {
                    id: 3,
                    name: "Runner",
                    blocks: [
                        {
                            id: 1,
                            name: "Vorbereitung"
                        }, {
                            id: 2,
                            name: "Routen"
                        }
                    ],
                    order: [1, 2,]
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 3,
            name: "Schichtplan",
            blocks: [
                {
                    id: 1,
                    name: "Schichten ändern",
                }, {
                    id: 2,
                    name: "Schichten anlegen",
                }, {
                    id: 3,
                    name: "Schichtplan"
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 4,
            name: "Ordner"
        }
    ]
    const [renderBlock, statusRenderBlock] = useState([]);

    const renderContent = (block) => {
        statusRenderBlock(block);
    }

    const iterateBlocks = (blocks) => (
        <ul>
            {
                Object.keys(blocks).map((i) => (
                    <li key={i} className="block">
                        <button onClick={() => renderContent(blocks[i])}>{blocks[i].name}</button>
                        {blocks[i].blocks && iterateBlocks(blocks[i].blocks)}
                    </li>
                ))
            }
        </ul>
    )

    return (
        <div id="SCC">
            <div id="Sidebar">
                {
                    iterateBlocks(options)
                }
            </div>
            <div id="Content">
                {renderBlock && (
                    <div>
                        {renderBlock.name}
                        {iterateBlocks(renderBlock.blocks)}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
