            blocks: [
                {
                    id: 1,
                    name: "FAQ"
                }, {
                    id: 2,
                    name: "Antwortblöcke"
                }, {
                    id: 3,
                    name: "Newsletter"
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 2,
            name: "Positionen",
            blocks: [
                {
                    id: 1,
                    name: "Kasse",
                    blocks: [
                        {
                            id: 1,
                            name: "Check-In"
                        }, {
                            id: 2,
                            name: "Check-Out"
                        }
                    ],
                    order: [1, 2]
                }, {
                    id: 2,
                    name: "Fenster",
                    blocks: [
                        {
                            id: 1,
                            name: "Check-In Pre"
                        }, {
                            id: 2,
                            name: "Check-In Runner-Zettel"
                        }, {
                            id: 3,
                            name: "Routen drucken"
                        }, {
                            id: 4,
                            name: "Packen"
                        }
                    ],
                    order: [1, 2, 3, 4]
                }, {
                    id: 3,
                    name: "Runner",
                    blocks: [
                        {
                            id: 1,
                            name: "Vorbereitung"
                        }, {
                            id: 2,
                            name: "Routen"
                        }
                    ],
                    order: [1, 2,]
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 3,
            name: "Schichtplan",
            blocks: [
                {
                    id: 1,
                    name: "Schichten ändern",
                }, {
                    id: 2,
                    name: "Schichten anlegen",
                }, {
                    id: 3,
                    name: "Schichtplan"
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 4,
            name: "Ordner"
        }
    ]
    const [renderBlock, statusRenderBlock] = [];

    const renderContent = (block) => {
        console.log(block)
        statusRenderBlock(block);
    }

    const iterateBlocks = (blocks) => (
        <ul>
            {
                Object.keys(blocks).map((i) => (
                    <li key={i} className="block">
                        <button onClick={() => renderContent(blocks[i])}>{ blocks[i].name}</button>
                        { blocks[i].blocks && iterateBlocks(blocks[i].blocks) }
                    </li>
                ))
            }
        </ul>
    )

    return (
        <div className="SCC">
            <div className="sidebar">
                {
                    iterateBlocks(options)
                }
            </div>
            <div>
                { renderBlock.name }
            </div>
        </div>
    );
}

export default App;
