import React, {useState, Fragment} from "react";
import './App.css';


function App() {
    let options = [
        {
            name: "Mails",
            blocks: [
                {
                    name: "FAQ"
                }, {
                    name: "Antwortblöcke"
                }, {
                    name: "Newsletter"
                }
            ]
        }, {
            name: "Positionen",
            blocks: [
                {
                    name: "Kasse",
                    blocks: [
                        {
                            name: "Check-In"
                        }, {
                            name: "Check-Out"
                        }
                    ]
                }, {
                    name: "Fenster",
                    blocks: [
                        {
                            name: "Check-In Pre"
                        }, {
                            name: "Check-In Runner-Zettel"
                        }, {
                            name: "Routen drucken"
                        }, {
                            name: "Packen"
                        }
                    ]
                }, {
                    name: "Runner",
                    blocks: [
                        {
                            name: "Vorbereitung"
                        }, {
                            name: "Routen"
                        }
                    ]
                }
            ]
        }, {
            name: "Schichtplan",
            blocks: [
                {
                    name: "Schichten ändern",
                }, {
                    name: "Schichten anlegen",
                }, {
                    name: "Schichtplan"
                }
            ]
        }, {
            name: "Ordner"
        }
    ]
    const [modifyBlocks, statusModifyBlocks] = useState(options);
    const [renderBlock, statusRenderBlock] = useState(modifyBlocks[0]);
    const [hideNav, statusHideNav] = useState(false);

    // Modify modifyBlocks
    const setOptionsPosition = (blocks, c) => {
        blocks.forEach((block, i) => {
            Object.assign(block, {position: c+i.toString()});
            block.blocks && setOptionsPosition(block.blocks, `${c+i.toString()} ` )
        })
    }
    setOptionsPosition(modifyBlocks, "")

    // Get block.position parent blocks
    const parentBlocks = () => {
        let blocks = []
        let block = modifyBlocks
        renderBlock.position.split(" ").forEach((i, c) => {
            block = c === 0 ? modifyBlocks[i] : block.blocks[i]
            blocks = [...blocks, block]
        })
        return blocks
    }

    const renderNavButton = (block, k) => (
        <button key={k || 0}
                onClick={() => {
                    statusRenderBlock(block);
                } }
                disabled={ block.position === renderBlock.position && block.blocks && block.blocks.length === 0}
                className={`navbarButton ${renderBlock.position.startsWith(block.position) && "active"}`}>{block.name}</button>
    )

    const renderSidebar = (blocks) => (
        <ul>
            {Object.keys(blocks).map((i) => blocks[i].name && (
                <li key={i} className="navBlock">
                    {renderNavButton(blocks[i])}
                    { blocks[i].blocks && renderBlock.position.startsWith(blocks[i].position) && renderSidebar(blocks[i].blocks)}
                </li>
            ))}
        </ul>
    )

    const getBlock = (blocks) => {
        console.log(blocks.position.split(" "))
        let execute
        blocks.position.split(" ").forEach((i, c) => {
            execute = c === 0 ? `modifyBlocks[${i}]` : execute+`.blocks[${i}]`
        })
        // eslint-disable-next-line no-eval
        return eval(execute)
    }

    const saveBlock = () => {
        statusModifyBlocks(modifyBlocks)
        statusRenderBlock(getBlock(renderBlock))
    }

    const addText = (block) => {
        Object.assign(getBlock(block), {text: "Hallo"});
        addElement()
        saveBlock()
    }

    const addElement = (element) => element && ({
        element
    })

    return (
        <div id="SCC">
            <div id="Sidebar" className={`${hideNav && "hidden"}`}>
                <button className={`hideSideBar ${ hideNav && "active"}`} onClick={() => statusHideNav(!hideNav)}>X</button>
                {
                    renderSidebar(modifyBlocks)
                }
            </div>
            <div id="TopBar">
                {parentBlocks().map((block, k) => renderNavButton(block, k) )}
            </div>
            <div id="Content">
                {renderBlock && (
                    <Fragment>
                        <button onClick={() => addText(renderBlock)}>Add Element</button>
                        <div className="col">
                            <h2>{renderBlock.name}</h2>
                        </div>
                        <div className="col">
                            {renderBlock.text}
                        </div>
                        <div className="col">
                            {renderBlock.blocks && renderSidebar(renderBlock.blocks)}
                        </div>
                    </Fragment>
                )}
            </div>
        </div>
    );
}

export default App;
