import React, {useState, Fragment} from "react";
import './App.css';


function App() {
    const options = [
        {
            id: 1,
            name: "Mails",
            blocks: [
                {
                    position: [1]
                },{
                    id: 1,
                    name: "FAQ"
                }, {
                    id: 2,
                    name: "Antwortblöcke"
                }, {
                    id: 3,
                    name: "Newsletter"
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 2,
            name: "Positionen",
            blocks: [
                {
                    position: [2]
                },{
                    id: 1,
                    name: "Kasse",
                    blocks: [
                        {
                            position: [2, 1]
                        },{
                            id: 1,
                            name: "Check-In"
                        }, {
                            id: 2,
                            name: "Check-Out"
                        }
                    ],
                    order: [1, 2]
                }, {
                    id: 2,
                    name: "Fenster",
                    blocks: [
                        {
                            position: [2, 2]
                        },{
                            id: 1,
                            name: "Check-In Pre"
                        }, {
                            id: 2,
                            name: "Check-In Runner-Zettel"
                        }, {
                            id: 3,
                            name: "Routen drucken"
                        }, {
                            id: 4,
                            name: "Packen"
                        }
                    ],
                    order: [1, 2, 3, 4]
                }, {
                    id: 3,
                    name: "Runner",
                    blocks: [
                        {
                            position: [2, 3]
                        },{
                            id: 1,
                            name: "Vorbereitung"
                        }, {
                            id: 2,
                            name: "Routen"
                        }
                    ],
                    order: [1, 2,]
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 3,
            name: "Schichtplan",
            blocks: [
                {
                    position: [3]
                },{
                    id: 1,
                    name: "Schichten ändern",
                }, {
                    id: 2,
                    name: "Schichten anlegen",
                }, {
                    id: 3,
                    name: "Schichtplan"
                }
            ],
            order: [1, 2, 3]
        }, {
            id: 4,
            name: "Ordner"
        }
    ]
    const [renderBlock, statusRenderBlock] = useState([]);
    const [unfoldNav, statusUnfoldNav] = useState([0]);

    const getNavPosition = (block, i) => {
        console.log(block[0].position, block[i])
        unfoldNav === 0 && console.log(unfoldNav)
        statusUnfoldNav(block[0].position || 0)
    }

    const iterateBlocks = (blocks) => (
        <ul>
            {
                Object.keys(blocks).map((i) => (
                    <Fragment key={i}>
                        { blocks[i].id && (
                            <li className="block">
                                <button onClick={() => statusRenderBlock(blocks[i])}>{blocks[i].name}</button>
                                <button onClick={() => getNavPosition(blocks, i)}>Get IDs</button>
                                { blocks[i].blocks && iterateBlocks(blocks[i].blocks)}
                            </li>
                        )}
                    </Fragment>
                ))
            }
        </ul>
    )

    const renderSidebar = (blocks) => (
        <ul>
            {
                Object.keys(blocks).map((i) => (
                    <React.Fragment key={i}>
                        { blocks[i].id && (
                            <li className="block">
                                <button onClick={() => statusRenderBlock(blocks[i])}>{blocks[i].name}</button>
                                <button onClick={() => getNavPosition(blocks, i)}>Get IDs</button>
                                { blocks[i].blocks && renderSidebar(blocks[i].blocks)}
                            </li>
                        )}
                    </React.Fragment>
                ))
            }
        </ul>
    )

    return (
        <div id="SCC">
            <div id="Sidebar">
                {
                    //iterateBlocks(options)
                    renderSidebar(options)
                }
            </div>
            <div id="Content">
                {unfoldNav}
                {renderBlock && (
                    <div>
                        {renderBlock.name}
                        {renderBlock.blocks && iterateBlocks(renderBlock.blocks)}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
