import React, {useState, Fragment} from "react";
import './App.css';


function App() {
    const options = [
        {
            name: "Mails",
            blocks: [
                {
                    name: "FAQ"
                }, {
                    name: "Antwortblöcke"
                }, {
                    name: "Newsletter"
                }
            ]
        }, {
            name: "Positionen",
            blocks: [
                {
                    name: "Kasse",
                    blocks: [
                        {
                            name: "Check-In"
                        }, {
                            name: "Check-Out"
                        }
                    ]
                }, {
                    name: "Fenster",
                    blocks: [
                        {
                            name: "Check-In Pre"
                        }, {
                            name: "Check-In Runner-Zettel"
                        }, {
                            name: "Routen drucken"
                        }, {
                            name: "Packen"
                        }
                    ]
                }, {
                    name: "Runner",
                    blocks: [
                        {
                            name: "Vorbereitung"
                        }, {
                            name: "Routen"
                        }
                    ]
                }
            ]
        }, {
            name: "Schichtplan",
            blocks: [
                {
                    name: "Schichten ändern",
                }, {
                    name: "Schichten anlegen",
                }, {
                    name: "Schichtplan"
                }
            ]
        }, {
            name: "Ordner"
        }
    ]

    const setOptionsPosition = (blocks, c) => {
        blocks.forEach((block, i) => {
            Object.assign(block, {position: c+i.toString()});
            block.blocks && setOptionsPosition(block.blocks, `${c+i.toString()} ` )
        })
    }
    setOptionsPosition(options, "")

    const [renderBlock, statusRenderBlock] = useState(options[0]);


    const renderSidebar = (blocks) => (
        <ul>
            {Object.keys(blocks).map((i) => blocks[i].name && (
                <li key={i} className="block">
                    <button onClick={() => statusRenderBlock(blocks[i]) }>{blocks[i].name}</button>
                    { blocks[i].blocks && renderBlock.position.startsWith(blocks[i].position) && renderSidebar(blocks[i].blocks)}
                </li>
            ))}
        </ul>
    )

    const renderTopBar = () => {
        console.log(renderBlock.position.split(" "))
        let topBar
        let block = options
        let prevBlock
        console.log(renderBlock.position)
        renderBlock.position.split(" ").forEach((i) => {
            topBar = topBar+<button>{prevBlock?.block[i]}</button>
            prevBlock = options[i]
            console.log(prevBlock)
        })
        return topBar
    }

    return (
        <div id="SCC">
            <div id="Sidebar">
                {
                    renderSidebar(options)
                }
            </div>
            <div id="TopBar">
                {
                    renderTopBar()
                }
            </div>
            <div id="Content">
                {renderBlock && (
                    <div>
                        {renderBlock.name}
                        {renderBlock.blocks && renderSidebar(renderBlock.blocks)}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
