import React, {useState, Fragment} from "react";
import './App.css';


function App() {
    const options = [
        {
            name: "Mails",
            blocks: [
                {
                    name: "FAQ"
                }, {
                    name: "Antwortblöcke"
                }, {
                    name: "Newsletter"
                }
            ]
        }, {
            name: "Positionen",
            blocks: [
                {
                    name: "Kasse",
                    blocks: [
                        {
                            name: "Check-In"
                        }, {
                            name: "Check-Out"
                        }
                    ]
                }, {
                    name: "Fenster",
                    blocks: [
                        {
                            name: "Check-In Pre"
                        }, {
                            name: "Check-In Runner-Zettel"
                        }, {
                            name: "Routen drucken"
                        }, {
                            name: "Packen"
                        }
                    ]
                }, {
                    name: "Runner",
                    blocks: [
                        {
                            name: "Vorbereitung"
                        }, {
                            name: "Routen"
                        }
                    ]
                }
            ]
        }, {
            name: "Schichtplan",
            blocks: [
                {
                    name: "Schichten ändern",
                }, {
                    name: "Schichten anlegen",
                }, {
                    name: "Schichtplan"
                }
            ]
        }, {
            name: "Ordner"
        }
    ]
    const [parentBlocks, statusParentBlocks] = useState([]);
    const [renderBlock, statusRenderBlock] = useState(options[0]);
    const setOptionsPosition = (blocks, c) => {
        blocks.forEach((block, i) => {
            Object.assign(block, {position: c+i.toString()});
            block.blocks && setOptionsPosition(block.blocks, `${c+i.toString()} ` )
        })
    }
    setOptionsPosition(options, "")

    const getParentBlocks = () => {
        let blocks = []
        let block = options
        renderBlock.position.split(" ").forEach((i, c) => {
            block = c === 0 ? options[i] : block.blocks[i]
            blocks = [...blocks, block[i].blocks]
        })
        console.log("##", block)
        console.log(renderBlock.position)
    }

    getParentBlocks()

    const renderSidebar = (blocks) => (
        <ul>
            {Object.keys(blocks).map((i) => blocks[i].name && (
                <li key={i} className="block">
                    <button onClick={() => statusRenderBlock(blocks[i]) }>{blocks[i].name}</button>
                    { blocks[i].blocks && renderBlock.position.startsWith(blocks[i].position) && renderSidebar(blocks[i].blocks)}
                </li>
            ))}
        </ul>
    )


    return (
        <div id="SCC">
            <div id="Sidebar">
                {
                    renderSidebar(options)
                }
            </div>
            <div id="TopBar">
                {parentBlocks && parentBlocks.map((block, k) => (
                    <button key={k} onClick={() => statusRenderBlock(block) }>{block?.name}</button>
                ))}
            </div>
            <div id="Content">
                {renderBlock && (
                    <div>
                        {renderBlock.name}
                        {renderBlock.blocks && renderSidebar(renderBlock.blocks)}
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
